<script setup lang="ts">
import { dapps } from "@/constants/dapps";
import { communities } from "@/constants/socials";

const featuredDapps = dapps.filter((dapp) => dapp.featured);

definePageMeta({
  layout: "top",
});

useSeoMeta({
  title: "Ecosystem",
  description:
    "Effect AI's ecosystem is a collection of all the applications running on the network.",
});

const { useDaoStatistics, useEfxPrice } = useStatistics();

const { data: price } = useEfxPrice();
const { currentCycle, feePoolBalance, proposalsCreated } = useDaoStatistics();

const daoStats = computed(
  () =>
    feePoolBalance.value &&
    price.value && [
      { label: "cycle", value: currentCycle.value },
      { label: "proposals", value: proposalsCreated.value },
      {
        label: "rewards",
        prefix: "$",
        value: formatNumber(feePoolBalance.value * price.value).value,
        suffix: formatNumber(feePoolBalance.value).suffix,
      },
    ]
);
</script>
